'use client'
import Image from 'next/image'
import React from 'react'

interface ProductCardProps {
  sellerImageSrc?: string
  title: string
  imageSrc: string | undefined
  price: string | undefined // price プロパティを追加
  brand: string | undefined
}

export const SalesInformationProductCardMobile: React.FC&lt;ProductCardProps&gt; = ({
  sellerImageSrc,
  imageSrc,
  title,
  price,
  brand,
}) =&gt; {
  return (
    &lt;div className='pa-10 px-21 container mx-auto flex  h-32 items-center gap-x-2 rounded border border-[#D9D9D9] bg-white py-2 hover:shadow-lg'&gt;
      &lt;div className='justify-center px-[21px] pb-[17px] pt-[12px]'&gt;
        {imageSrc ? (
          &lt;Image
            src={imageSrc}
            alt={title}
            height={100}
            width={100}
            placeholder='blur'
            className='max-w-80'
            blurDataURL='data:image/png;base64,iVBORw0KG...' // プレースホルダーのブラーエフェクトに使用するダミー画像データ
          /&gt;
        ) : (
          &lt;&gt;No Image&lt;/&gt;
        )}
       &lt;/div&gt;
      &lt;div className='pa-10 px-14 container mx-auto flex  h-32 items-center gap-x-2 rounded border border-[#D9D9D9] bg-white py-2 hover:shadow-lg'&gt;
      &lt;div className='justify-center px-[21px] pb-[17px] pt-[12px]'&gt;&lt;/div&gt;

        {sellerImageSrc ? (
          &lt;Image
            src={sellerImageSrc}
            alt={title}
            width={150}
            height={150}
            placeholder='blur'
            blurDataURL='data:image/png;base64,iVBORw0KG...' // プレースホルダーのブラーエフェクトに使用するダミー画像データ
          /&gt;
        ) : (
          &lt;&gt;No Image&lt;/&gt;
        )}
      &lt;/div&gt;
      &lt;div className='flex flex-1 justify-center'&gt;

        &lt;div&gt;
          &lt;p className='text-[13px]'&gt;{brand}&lt;/p&gt;
          &lt;p className='text-[13px]'&gt;{title}&lt;/p&gt;
          &lt;br /&gt;
          &lt;p className='text-[10px]'&gt;価格: {price}&lt;/p&gt; {/* "価格" プロパティを表示 */}
          &lt;p className='text-[10px]'&gt;メーカー名: {brand}&lt;/p&gt;
        &lt;/div&gt;
      &lt;/div&gt;
    &lt;/div&gt;
  )
}

